mapper.select:
select w.worker_id,w.worker_account,w.worker_password,w.worker_name,w.worker_createtimr, w.worker_phone,w.worker_age,w.worker_address, (select r.role_name from t_role as r where r.role_id=w.role_id ) roleName, (select p.param_name from t_param as p where p.param_value= w.worker_state )StateName, (select pp.param_name from t_param as pp where pp.param_value=w.worker_sex)sexName from t_worker as w where 1=1
and w.worker_name like concat('%',#{condition.adminName},'%')
and w.worker_account like concat('%',#{condition.account},'%')
and w.worker_phone like concat('%',#{condition.phone},'%')
and w.role_id=#{condition.n}
limit #{curPage},#{pageSize};
