mapper.select:
select d.detail_id,d.detail_carnumber,d.detail_event,d.produce_id,d.detail_time,d.detail_money,d.detail_type,d.worker_id,(select w.worker_name from t_worker as w where w.worker_id=d.worker_id)workerName from t_detail as d where d.detail_time < (CURDATE()+1)  and d.detail_time>CURDATE()

    and d.detail_carnumber like concat('%',#{condition.personCarnumber},'%')


    and d.detail_time >= #{condition.date1}



and d.detail_time <= #{condition.date2}



    and d.worker_id=(select w.worker_id from t_worker as w where w.worker_name=#{condition.workerNames})


    limit #{curPage},#{pageSize};

select count(d.detail_id) from t_detail as d where d.detail_time < (CURDATE()+1)  and d.detail_time>CURDATE()

    and d.detail_carnumber like concat('%',#{condition.personCarnumber},'%')


    and d.detail_time >= #{condition.date1}



and d.detail_time <= #{condition.date2}



    and d.worker_id=(select w.worker_id from t_worker as w where w.worker_name=#{condition.workerNames})
